# Extract base64 encoded image from Google Docs markdown export
# and save as PNG file

$base64Data = @"

"@

$bytes = [Convert]::FromBase64String($base64Data)
$imagePath = "c:\Users\MartinHinshelwoodNKD\source\repos\ScrumGuide-ExpansionPack\site\content\product-thinking\2026.1\images\value-chain-feedback-loop.png"

try {
    [System.IO.File]::WriteAllBytes($imagePath, $bytes)
    Write-Host "✓ Image successfully extracted to: $imagePath" -ForegroundColor Green
    Write-Host "  File size: $($bytes.Length) bytes" -ForegroundColor Cyan
} catch {
    Write-Host "✗ Error: $_" -ForegroundColor Red
}
